
public class Multi_Array3_book_p95 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int []num;
		num = new int[]	 {1,2,3};
		
		for(int x : num) {
			System.out.println("정수 배열 값 :" + x);
		}
		String[]name=new String[] {"홍길동","이순신","유관순"};
		for (String s : name) {
			System.out.println("문자열 배열 값 : " + s);
		}
    }
}